import React from 'react'

export const Button = (props) => {
  let cl = "btn btn-";
  if(props.out)
    cl = cl + "outline-";
  cl = cl + props.v;

  let styles = {
    'border-radius': props.br,
    'width': props.w + '%',
    'height': '40px'
  };

  return (
    <div>
        <button className={cl} style={styles}>{props.c}</button>
    </div>
  )
}

Button.defaultProps = {
  out: true,
  v: 'secondary',
  br: '25px',
  w: 15
}
