import React from 'react'

export const Button = (props) => {
  let cl = "btn btn-";
  if(props.out)
    cl = cl + "outline-";
  cl = cl + props.v;

  let styles = {
    'border-radius': props.br,
    'width': props.w + '%',
    'height': '40px'
  };

  return (
    <div style={{"margin": "100px"}}>
        <a class="inline-block rounded px-8 py-3 text-sm font-medium text-white transition hover:scale-110 hover:shadow-xl focus:outline-none focus:ring active:bg-indigo-500"
            href="/download" style={{"background-color": "rgb(2, 181, 53)"}}>
                {props.c}
        </a>
    </div>
  )
}

Button.defaultProps = {
  out: true,
  v: 'secondary',
  br: '25px',
  w: 15
}
